}

void MiNodeRGB::rgbClockWrite(int value)
{
  CLOCK->write(value);
}

void MiNodeRGB::rgbDataWrite(int value)
{
  DATA->write(value);
}

void MiNodeRGB::rgbDelay()
{

}

void MiNodeRGB::chooseColor(MiNodeColor c)
{
  switch(c)
  {
    case MiNode_RGB_COLOR_RED:
      rgbWrite(255,0,0);
      break;
    case MiNode_RGB_COLOR_GREEN:
      rgbWrite(0,255,0);
      break;
    case MiNode_RGB_COLOR_BLUE:
      rgbWrite(0,0,255);
      break;
    case MiNode_RGB_COLOR_YELLOW:
      rgbWrite(255,255,0);
      break;
    case MiNode_RGB_COLOR_PINK:
      rgbWrite(255,0,255);
      break;
    case MiNode_RGB_COLOR_CYAN:
      rgbWrite(0,255,255);
      break;
    case MiNode_RGB_COLOR_WHITE:
      rgbWrite(255,255,255);
      break;
    default :
      rgbWrite(255,0,0);
      break;
  }
}

void MiNodeRGB::setRGBInHex(unsigned int color)
{
  int red=0;
  int green=0;
  int blue=0;

  red = color/0x10000;
  green = color%0x10000/0x100;
  blue = color%0x100;

  rgbWrite(red,green,blue);
}

void MiNodeRGB::setRGB(uint8_t r,uint8_t g,uint8_t b)
{
  rgbWrite(r,g,b);
}

void MiNodeRGB::rgbWrite(uint8_t r,uint8_t g,uint8_t b)
{
  uint32_t rgb = 0;

  // set bit 7,15,23
  rgb = (1<<23) | (1<<15) | (1<<7);

  // be divided by 2 because the chip only need 7 bits
  r>>=1;
  g>>=1;
  b>>=1;

  rgb |= (g << 15) | (r << 7) | (b << 0);

  rgbClockWrite(0); //P15
  rgbDataWrite(0);  //P16

  rgbDelay();

  // out put at least 32 zeros
  for(int i = 0; i < 32; i ++) {
      rgbDataWrite(0);
      rgbDelay();
      rgbClockWrite(1);
      rgbDelay();
      rgbClockWrite(0);
  }

  rgbDelay();

  // out put 24 bits RGB data to channel 1,2,3.
  for(int i = 0; i < 24; i ++) {
      if(rgb & (1 << 23))
          rgbDataWrite(1);
      else
          rgbDataWrite(0);
      rgb <<= 1;

      rgbDelay();
      rgbClockWrite(1);
      rgbDelay();
      rgbClockWrite(0);
  }

  rgbDelay();

  // out put 24 bit rgb data for channel 4,5,6. data value is ignored. here set to 0
  rgb = 0;
  rgb = (1<<23) | (1<<15) | (1<<7);
  for(int i = 0; i < 24; i ++) {
      if(rgb & (1 << 23))
          rgbDataWrite(1);
      else
          rgbDataWrite(0);
      rgb <<= 1;

      rgbDelay();
      rgbClockWrite(1);
      rgbDelay();
      rgbClockWrite(0);
  }
}

